//string key1 = "DIAPHRaGM";
//string key2 = "LABOUR";
string key1 = "RAISE";
string key2 = "COW";

key1 = String.Concat(key1.ToUpper().Replace('Q', 'K').Distinct());
key2 = String.Concat(key2.ToUpper().Replace('Q', 'K').Distinct());

// 4 dictionaries
char[,] plainGrid = { { 'A', 'B', 'C', 'D', 'E' },
                        { 'F', 'G', 'H', 'I', 'J' },
                        { 'K', 'L', 'M', 'N', 'O' },
                        { 'P', 'R', 'S', 'T', 'U' },
                        { 'V', 'W', 'X', 'Y', 'Z' }, };

int[,] key1Pattern = { { 0, 9, 16, 21, 24},
                            {5, 1, 10, 17, 22 },
                            {13, 6, 2, 11, 18 },
                            {19, 14, 7, 3, 12 },
                            {23, 20, 15, 8, 4 }};

int[,] key2Pattern = { { 4, 12, 18, 22, 24},
                            {8, 3, 11, 17, 21 },
                            {15, 7, 2, 10, 16 },
                            {20, 14, 6, 1, 9 },
                            {23, 19, 13, 5, 0 }};


char[,] upperRightGrid = new char[5, 5];
char[,] lowerLeftGrid = new char[5, 5];


// Construct upper right grid
var chars = new Queue<char>("ABCDEFGHIJKLMNOPRSTUVWXYZ".Except(key1));

for (int i = 0; i < 5; i++)
{
    for (int j = 0; j < 5; j++)
    {
        var keywordIndex = key1Pattern[i, j];
        if (keywordIndex < key1.Length) upperRightGrid[i, j] = key1[keywordIndex];
        else upperRightGrid[i,j] = chars.Dequeue();
    }
}

// Construct lower left grid
var chars2 = new Queue<char>("ABCDEFGHIJKLMNOPRSTUVWXYZ".Except(key2));

for (int i = 0; i < 5; i++)
{
    for (int j = 0; j < 5; j++)
    {
        var keywordIndex = key2Pattern[i, j];
        if (keywordIndex < key2.Length) lowerLeftGrid[i, j] = key2[keywordIndex];
        else lowerLeftGrid[i, j] = chars2.Dequeue();
    }
}


// Remove spaces, make upper case, and pad
var simplifiedMessage = message.Replace(" ", "");
var plaintext = simplifiedMessage.PadRight((int)Math.Ceiling((double)(simplifiedMessage.Length)/2)*2, 'X').ToUpper().Replace('Q', 'K');
var encryptedMessageChars = new char[plaintext.Length];

var pointer = 0;
while (pointer < plaintext.Length)
{
    (int alpha, int beta) = plainGrid.CoordinatesOf(plaintext[pointer]);
    (int gamma, int delta) = plainGrid.CoordinatesOf(plaintext[pointer + 1]);

    encryptedMessageChars[pointer] = upperRightGrid[alpha, delta];
    encryptedMessageChars[pointer+1] = lowerLeftGrid[gamma, beta];

    pointer = pointer + 2;
}

var encryptedMessage = String.Concat(encryptedMessageChars);

Console.WriteLine(encryptedMessage);


// Frequency analysis
var digraphs = Enumerable.Range(0, encryptedMessage.Length/2).Select(x => encryptedMessage.Substring(x * 2, 2));
var frequencies = from x in digraphs group x by x into g let count = g.Count() orderby count descending select new {Digraph = g.Key, Count = g.Count() };

Console.WriteLine(digraphs);

